import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {Room, TypeOfEvent} from '../functional/EnumsAndConsts'
import Calendar from "./Calendar";
import '../resource/styles/ApplicationForm.css'

class FromTo {
    constructor(from, to) {
        this.from = from;
        this.to = to;
    }
}

class ApplicationForm extends Component {

    constructor(props) {
        super(props);

        this.calendarRef = React.createRef();
    }

    state = {
        eventName: null,
        eventType: TypeOfEvent.CONCERT,
        rooms: [],
        viewersExpected: false,
        viewers: 0,
        comment: ''
    };

    render() {
        return (
            <div id='application-form' onMouseDown={e => this.startFormDrag(e)}>
                <button onClick={this.props.closeAppWindow}>X</button><br/>
                <label>Название мероприятия: </label>
                <input type='text' name='eventName' onChange={(e) => {this.handleInput(e)}}/><br/>
                <label>Тип мероприятия: </label>
                <select name='eventType' onChange={(e) => {this.handleInput(e)}}>
                    <option value={TypeOfEvent.CONCERT}>Концерт, представление</option>
                    <option value={TypeOfEvent.PARTY}>Корпоратив</option>
                    <option value='SPORT'>Спорт</option>
                    <option value={TypeOfEvent.OTHER}>Другое</option>
                </select><br/>
                {['SPORT', 'MATCH', 'TRAINING'].includes(this.state.eventType)
                    ?
                    <React.Fragment>
                        <input name='eventType' type='radio' value='TRAINING' onChange={(e) => {this.handleInput(e)}}/><label>Тренировка</label><br/>
                        <input name='eventType' type='radio' value='MATCH' onChange={(e) => {this.handleInput(e)}}/><label>Матч</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                <label>Помещение: </label>
                <select name='room' onChange={(e) => {this.addRoom(e)}}>
                    {Room.map((roomInfo) =>
                        <option key={roomInfo.stringId} value={roomInfo.stringId}>{roomInfo.rusName}</option> )}
                </select><br/>
                {this.state.rooms.map(roomName => {
                    return <span key={roomName}>{`${roomName}   `}</span>
                })}
                {this.state.eventType === TypeOfEvent.OTHER
                    ?
                    <React.Fragment>
                        <label>Ожидается ли на Вашем мероприятии зрительская аудитория?</label><br/>
                        <input name='viewersExpected' type='radio' value={true} onChange={(e) => {this.handleInput(e)}}/><label>Да</label><br/>
                        <input name='viewersExpected' type='radio' value={false} onChange={(e) => {this.handleInput(e)}}/><label>Нет</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                {['MATCH', 'PARTY'].includes(this.state.eventType) || this.state.viewersExpected === 'true'
                    ?
                    <React.Fragment>
                        <label>Какое количество зрителей Вы предполагаете?</label>
                        <input type='text' name='viewers' onChange={(e) => {this.handleInput(e)}}/><br/>
                    </React.Fragment>
                    :
                    null
                }
                <Calendar ref={this.calendarRef}/><br/>
                <input type='text' name='comment' onChange={e => this.handleInput(e)}/>
                <button onClick={e => this.sendApplication(e)}>send</button>
            </div>
        );
    }

    handleInput = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        });
    };

    sendApplication = () => {
        const nameJSON = `"name":"${this.state.eventName}",`;
        const audJSON = `"auditory":${this.state.viewers},`;
        const typeJSON = `"type":"${this.state.eventType}",`;
        const roomsJSON = `"rooms":${JSON.stringify(this.state.rooms)},`;
        const userJSON = `"user":"${this.props.userName}",`;

        const datesArraySel = this.calendarRef.current.state.selectedDays;
        const timesArraySel = this.calendarRef.current.state.selectedTimings;
        const dateArrJSON = JSON.stringify(JSON.stringify(this.dateTimeJSON(datesArraySel, timesArraySel)));
        const dateJSON = `"dates":${dateArrJSON},`;

        const commentJSON = `"comment":"${this.state.comment}"`;

        const toSend = '{' + nameJSON + audJSON + typeJSON + roomsJSON + userJSON + dateJSON + commentJSON + '}';

        const request = new XMLHttpRequest();

        request.onreadystatechange = function()
        {
            if (request.readyState === 4)
            {
                console.log('OK!');
            }
        };

        request.open('POST', 'http://siburarenda.publicvm.com/api/user/order', true);
        request.setRequestHeader('Authorization', 'Bearer_' + this.props.token);
        request.setRequestHeader('Content-Type', 'application/json');
        request.send(toSend)
    };

    dateTimeConstruction = (datesArray, timesArray) => {
        const datesTimesArrayFrom = [];
        const dateTimesArrayTo = [];
        for (let i = 0; i < datesArray.length; i++) {
            const dividedDate = datesArray[i].split(' ');
            const t = timesArray[i];
            const stringRepFrom = `${dividedDate[0]} ${dividedDate[1]} ${dividedDate[2]} ${this.leadZero(t.startH)}:${this.leadZero(t.startM)}:00 NOVT ${dividedDate[3]}`;
            const stringRepTo = `${dividedDate[0]} ${dividedDate[1]} ${dividedDate[2]} ${this.leadZero(t.endH)}:${this.leadZero(t.endM)}:00 NOVT ${dividedDate[3]}`;
            datesTimesArrayFrom.push(stringRepFrom);
            dateTimesArrayTo.push(stringRepTo);
        }
        return [datesTimesArrayFrom, dateTimesArrayTo];
    };

    dateTimeJSON = (dates, times) => {
        const dt = this.dateTimeConstruction(dates, times); // [0] = from, [1] = to
        const res = [];
        for (let i = 0; i < dt[0].length; i++) {
            res.push(new FromTo(dt[0][i], dt[1][i]));
        }
        return res;
    };

    leadZero = (num) => {
        let ans = num + "";
        while (ans.length < 2){
            ans = '0' + ans;
        }
        return ans;
    };

    addRoom = (e) => {
        if (!this.state.rooms.includes(e.target.value)) {
            this.setState({
                rooms: [...this.state.rooms, e.target.value]
            })
        }
    };

    startFormDrag = (e) => {
        console.log(`e.screenX: ${e.screenX}, e.screenY: ${e.screenY}, e.target: ${e.target}`)
    }
}

ApplicationForm.propTypes = {
    closeAppWindow: PropTypes.func.isRequired,
    userName: PropTypes.string.isRequired,
    token: PropTypes.string.isRequired
};

export default ApplicationForm;
