import React, {Component} from 'react';
import PropTypes from 'prop-types';
import Timing from "../functional/Timing";

class TimeSelectDialogue extends Component {

    state = {
        sh: this.props.dayToShow.startH,
        sm: this.props.dayToShow.startM,
        eh: this.props.dayToShow.endH,
        em: this.props.dayToShow.endM
    };

    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
        const {sh, sm, eh, em} = this.state;
        this.props.changeTiming.bind(new Timing(sh, sm, eh, em))
    };

    render() {
        return (
            <div>
                <label>Время начала мероприятия: </label>
                <input
                    defaultValue={this.props.dayToShow.startH}
                    type='number'
                    min={0}
                    max={23}
                    id='startTimeH'
                    name='startTimeH'
                    onChange={this.handleInput}
                />
                <label> ч </label>
                <input
                    defaultValue={this.props.dayToShow.startM}
                    type='number'
                    min={0}
                    max={59}
                    id='startTimeM'
                    name='startTimeM'
                    onChange={this.handleInput}
                />
                <label> мин</label><br/>
                <label>Время окончания мероприятия: </label>
                <input
                    defaultValue={this.props.dayToShow.endH}
                    type='number'
                    min={0}
                    max={23}
                    id='endTimeH'
                    name='endTimeH'
                    onChange={this.handleInput}
                />
                <label> ч </label>
                <input
                    defaultValue={this.props.dayToShow.endM}
                    type='number'
                    min={0}
                    max={59}
                    id='endTimeM'
                    name='endTimeM'
                    onChange={this.handleInput}
                />
                <label> мин</label><br/>
                <input type='checkbox' onChange={(e) => {console.log(e.target.value)}}/>
                <label>Установить такие временные рамки для всех выбранных дней</label><br/>
                <button onClick={()=> {this.props.closeTimeSelectDialogue()}}>OK</button>
            </div>
        );
    }
}

TimeSelectDialogue.propTypes = {
    dayToShow: PropTypes.object.isRequired,
    changeTiming: PropTypes.func.isRequired,
    setForAll: PropTypes.func.isRequired,
    closeTimeSelectDialogue: PropTypes.func.isRequired
};

export default TimeSelectDialogue;
