import React, {Component} from 'react';
import PropTypes from 'prop-types';
import SelectList from "./SelectList";

class Dropdown extends Component {

    constructor(props) {
        super(props);

        this.selectListRef = React.createRef();
    }

    state = {
        showSelect: false,
        chosen: this.props.options[0]
    };

    componentWillMount() {
        document.addEventListener('click', e => this.handleClickOutside(e), false);
    }

    componentWillUnmount() {
        document.removeEventListener('click', e => this.handleClickOutside(e), false);
    }

    handleClickOutside(e) {
        // const domNode = this.selectListRef.current;
        //
        // if (domNode != null && !domNode.contains(event.target)) {
        //     this.setState({
        //         showSelect : false
        //     });
        // }

        console.log('click outside select popup! e is ' + e)
    }

    render() {
        return (
            <span
                className='dropdown hover-text'
                onClick={() => {if (!this.props.withButton) this.openSelect()}}
            >
                {this.props.header === '' ? this.state.chosen.rusName : this.props.header}
                {this.props.withButton
                    ?
                    <button
                        className='open-select-btn'
                        onClick={() => this.openSelect()}
                    >
                        jopa
                    </button>
                    : null}
                {this.state.showSelect
                    ?
                    <SelectList
                        options={this.props.options}
                        onChoose={this.choose}
                        ref={this.selectListRef}
                    />
                    : null
                }
            </span>
        );
    }

    openSelect() {
        this.setState({
            showSelect: !this.state.showSelect
        })
    }

    choose = (option) => {
        this.setState({
            chosen: option,
            showSelect: false
        });
        this.props.onChoose(option);
    }
}

Dropdown.propTypes = {
    header: PropTypes.string.isRequired,
    options: PropTypes.array.isRequired,
    onChoose: PropTypes.func.isRequired,
    withButton: PropTypes.bool.isRequired
};

export default Dropdown;
