import React, {Component} from 'react';
import PropTypes from 'prop-types';
import SelectList from "./SelectList";

class Dropdown extends Component {

    state = {
        showSelect: false,
        chosen: this.props.options[0]
    };

    handleClickOutside(e) {
        if (e.target.classList.indexOf('select-popup') === -1) {
            this.setState({
                showSelect: false
            })
        }
    }

    render() {
        return (
            <span
                className='dropdown hover-text'
                onClick={() => {if (!this.props.withButton) this.openSelect()}}
            >
                {this.props.header === '' ? this.state.chosen.rusName : this.props.header}
                {this.props.withButton
                    ?
                    <button
                        className='open-select-btn'
                        onClick={() => this.openSelect()}
                    >
                        jopa
                    </button>
                    : null}
                {this.state.showSelect
                    ?
                    <SelectList
                        options={this.props.options}
                        onChoose={this.choose}
                        clickOutside={this.handleClickOutside}
                        left={this.props.left}
                    />
                    : null
                }
            </span>
        );
    }

    openSelect() {
        this.setState({
            showSelect: !this.state.showSelect
        })
    }

    choose = (option) => {
        this.setState({
            chosen: option,
            showSelect: false
        });
        this.props.onChoose(option);
    }
}

Dropdown.propTypes = {
    header: PropTypes.string.isRequired,
    options: PropTypes.array.isRequired,
    onChoose: PropTypes.func.isRequired,
    withButton: PropTypes.bool.isRequired,
    left: PropTypes.number.isRequired
};

export default Dropdown;
