import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {Room, TypeOfEvent} from '../functional/EnumsAndConsts'
import Calendar from "./Calendar";
import '../resource/styles/ApplicationForm.css'

class ApplicationForm extends Component {

    state = {
        eventName: null,
        eventType: TypeOfEvent.CONCERT,
        rooms: [],
        viewersExpected: false,
        viewers: 0
    };

    render() {
        return (
            <form id='application-form'>
                <button onClick={this.props.closeAppWindow}>X</button><br/>
                <label>Название мероприятия: </label>
                <input type='text' name='eventName' onChange={(e) => {this.handleInput(e)}}/><br/>
                <label>Тип мероприятия: </label>
                <select name='eventType' onChange={(e) => {this.handleInput(e)}}>
                    <option value={TypeOfEvent.CONCERT}>Концерт, представление</option>
                    <option value={TypeOfEvent.PARTY}>Корпоратив</option>
                    <option value='sport'>Спорт</option>
                    <option value={TypeOfEvent.OTHER}>Другое</option>
                </select><br/>
                {['sport', 'match', 'training'].includes(this.state.eventType)
                    ?
                    <React.Fragment>
                        <input name='eventType' type='radio' value='training' onChange={(e) => {this.handleInput(e)}}/><label>Тренировка</label><br/>
                        <input name='eventType' type='radio' value='match' onChange={(e) => {this.handleInput(e)}}/><label>Матч</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                <label>Помещение: </label>
                <select name='room' defaultValue={'Выберите комнату...'} onSelect={(e) => {this.addRoom(e)}}>
                    {Room.map((roomInfo) =>
                        <option key={roomInfo.stringId} value={roomInfo.stringId}>{roomInfo.rusName}</option> )}
                </select><br/>
                {this.state.rooms.map(roomName => {
                    return <span key={roomName}>{`${roomName}   `}</span>
                })}
                {this.state.eventType === TypeOfEvent.OTHER
                    ?
                    <React.Fragment>
                        <label>Ожидается ли на Вашем мероприятии зрительская аудитория?</label><br/>
                        <input name='viewersExpected' type='radio' value={true} onChange={(e) => {this.handleInput(e)}}/><label>Да</label><br/>
                        <input name='viewersExpected' type='radio' value={false} onChange={(e) => {this.handleInput(e)}}/><label>Нет</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                {['match', 'party'].includes(this.state.eventType) || this.state.viewersExpected === 'true'
                    ?
                    <React.Fragment>
                        <label>Какое количество зрителей Вы предполагаете?</label>
                        <input type='text' name='viewers' onChange={(e) => {this.handleInput(e)}}/><br/>
                    </React.Fragment>
                    :
                    null
                }
                <Calendar/>
                <input type='submit' value='Отправить заявку' onClick={this.sendApplication}/>
            </form>
        );
    }

    handleInput = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        });
    };

    sendApplication = (e) => {
        e.preventDefault();
        console.log(this.state);
    };

    addRoom = (e) => {
        this.setState({
            rooms: [...this.state.rooms, e.target.value]
        })
    };
}

ApplicationForm.propTypes = {
    closeAppWindow: PropTypes.func.isRequired
};

export default ApplicationForm;
