import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Link} from "react-router-dom"
import './resource/styles/Main.css'
import About from "./components/client/About";
import Rooms from "./components/client/Rooms";
import Contacts from "./components/client/Contacts"
import SignUp from "./components/client/SignUp";
import ApplicationForm from "./components/client/ApplicationForm";
import LogIn from "./components/client/LogIn";
import ClientNavList from "./components/client/ClientNavList";
import AdminNavList from "./components/admin/AdminNavList";
import ClientMainContent from "./components/client/ClientMainContent";

class App extends Component {

    state = {
        applicationFormVisible: false,
        logInFormVisible: false,
        userName: '',
        userLogin: '',
        token: ''
    };

    render() {
        return (
            <Router>

                <header>
                    <div className='container'>
                        <div id='top-panel'>
                            <button onClick={() => {this.openLogInForm()}}>Войти</button>
                            <Link to='/admin'>Войти как администратор</Link>
                        </div>
                    </div>
                </header>

                <nav>
                    <img src='/src/resource/img/siburLogoTemp.png'/>
                    <ul id='main-nav'>
                        <Route
                            path={/^\/(?!admin).*/}
                            render={(props) => (<ClientNavList {...props} openApplicationForm={this.openApplicationForm}/>)}
                        />
                        <Route
                            path='/admin'
                            render={(props) => (<AdminNavList {...props}/>)}
                        />
                    </ul>
                </nav>

                <section id='main-content'>
                    <div className='container'>
                        <Route
                            path={/^\/(?!admin).*/}
                            component={ClientMainContent}
                        />
                    </div>
                </section>

            </Router>
        );
    }

    openApplicationForm = () => {
        this.setState({
            applicationFormVisible: true
        })
    };

    closeApplicationForm = () => {
        this.setState({
            applicationFormVisible: false
        })
    };

    openLogInForm = () => {
        this.setState({
            logInFormVisible: true
        })
    };

    closeLogInForm = () => {
        this.setState({
            logInFormVisible: false
        })
    };

    storeResponse = (username, token) => {
        this.setState({
            userName: username,
            token: token
        })
    };
}

export default App;
