import React from 'react';
import moment from 'moment';

class Calendar extends React.Component {
    state = {
        dateContext: moment(),
        today: moment(),
        showMonthPopup: false,
        showYearPopup: false,
        selectedDays: []
    };

    weekdaysShort = moment.weekdaysShort();
    months = moment.months();

    year = () => this.state.dateContext.format("Y");

    month = () => this.state.dateContext.format("MMMM");

    daysInMonth = () => this.state.dateContext.daysInMonth();

    currentDay = () => this.state.dateContext.format("D");

    firstDayOfMonth = () => moment(this.state.dateContext).startOf('month').format('d');

    setMonth = (month) => {
        const monthNo = this.months.indexOf(month);
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("month", monthNo);
        this.setState({
            dateContext: dateContext
        });
    };

    moveMonth = (side) => {
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).add(side, "month");
        this.setState({
            dateContext: dateContext
        });
    };

    SelectList = (props) => {
        const popup = props.monthList.map((monthName) => {
            return (
                <div key={monthName}>
                    <p onClick={() => {this.setMonth(monthName)}}>
                        {monthName}
                    </p>
                </div>
            );
        });

        return (
            <div>
                {popup}
            </div>
        );
    };

    onChangeMonth = () => {
        this.setState({
            showMonthPopup: !this.state.showMonthPopup
        });
    };

    MonthNav = () => {
        return (
            <span onClick={()=> {this.onChangeMonth()}}>
                {this.month()}
                {this.state.showMonthPopup
                    ? <this.SelectList monthList={this.months} />
                    : null
                }
            </span>
        );
    };

    showYearEditor = () => {
        this.setState({
            showYearNav: true
        });
    };

    setYear = (e) => {
        const userInput = e.target.value;
        if (/\d+/.test(userInput)) {
            let dateContext = Object.assign({}, this.state.dateContext);
            dateContext = moment(dateContext).set("year", e.target.value);
            this.setState({
                dateContext: dateContext
            });
        }
    };

    onKeyUpYear = (e) => {
        if (e.which === 13 || e.which === 27) {
            this.setState({
                showYearNav: false
            });
        }
    };

    YearNav = () => {
        return (
            this.state.showYearNav
                ?
                <input
                    defaultValue = {this.year()}
                    onKeyUp={(e) => this.onKeyUpYear(e)}
                    onChange={(e) => this.setYear(e)}
                    type="number"
                />
                :
                <span
                    onClick={()=> { this.showYearEditor()}}>
                {this.year()}
                </span>
        );
    };

    onDayClick = (day) => {
        this.setState({
            selectedDay: day
        });
    };

    render() {
        const weekdays = this.weekdaysShort.map((day) => {
            return (
                <td key={day}>{day}</td>
            )
        });

        const blanks = [];
        for (let i = 0; i < this.firstDayOfMonth(); i++) {
            blanks.push(<td key={i * 80}>{""}</td>);
        }

        const daysInMonth = [];
        for (let d = 1; d <= this.daysInMonth(); d++) {
            let className = 'day';
            if (this.state.selectedDays.includes(d)) {
                className += 'selected-day';
            }
            // const selectedClass = (d === this.state.selectedDay ? " selected-day " : "");
            daysInMonth.push(
                <td key={d} /*className={className + selectedClass}*/ >
                    <span onClick={()=>{this.onDayClick(d)}}>{d}</span>
                </td>
            );
        }

        const totalSlots = [...blanks, ...daysInMonth];
        const rows = [];
        let cells = [];

        totalSlots.forEach((row, i) => {
            if ((i % 7) !== 0) {
                cells.push(row);
            } else {
                rows.push(cells.slice());
                cells = [];
                cells.push(row);
            }
            if (i === totalSlots.length - 1) {
                rows.push(cells.slice());
            }
        });

        const trElements = rows.map((d, i) => {
            return (
                <tr key={i*100}>
                    {d}
                </tr>
            );
        });

        console.log(this.state.dateContext.format('dddd'))

        return (
            <div>
                <table>
                    <thead>
                    <tr>
                        <td colSpan="5">
                            <this.MonthNav />
                            {" "}
                            <this.YearNav />
                        </td>
                        <td colSpan="2">
                            <button onClick={()=> {this.moveMonth(-1)}}>prev</button>
                            <button onClick={()=> {this.moveMonth(1)}}>next</button>
                        </td>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        {weekdays}
                    </tr>
                    {trElements}
                    </tbody>
                </table>

            </div>

        );
    }
}

export default Calendar;
