import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Link} from "react-router-dom"
import './resource/styles/Main.css'
import About from "./components/client/About";
import Rooms from "./components/client/Rooms";
import Contacts from "./components/client/Contacts"
import SignUp from "./components/client/SignUp";
import ApplicationForm from "./components/client/ApplicationForm";
import LogIn from "./components/client/LogIn";
import ClientNavList from "./components/client/ClientNavList";
import AdminNavList from "./components/admin/AdminNavList";

class App extends Component {

    state = {
        applicationFormVisible: false,
        logInFormVisible: false,
        userName: '',
        userLogin: '',
        token: '',
        admin: false
    };

    render() {
        return (
            <Router>

                <header>
                    <div className='container'>
                        <div id='top-panel'>
                            <button onClick={() => {this.openLogInForm()}}>Войти</button>
                            <Link to='/admin' onClick={() => this.asAdmin()}>Войти как администратор</Link>
                        </div>
                    </div>
                </header>

                <nav>
                    <img src='/src/resource/img/siburLogoTemp.png'/>
                    <ul id='main-nav'>
                        <Route
                            path='/'
                            render=
                                {
                                    this.state.admin
                                        ? null
                                        : (props) => (<ClientNavList {...props} openApplicationForm={this.openApplicationForm}/>)
                                }
                        />
                        <Route path='/admin' render=
                            {
                                this.state.admin
                                    ? (props) => (<AdminNavList {...props}/>)
                                    : null
                            }
                        />
                    </ul>
                </nav>

                <section id='mainContent'>
                    <div className='container'>
                        <Route
                            path="/"
                            exact
                            component={About}
                        />
                        <Route
                            path="/rooms"
                            component={Rooms}
                        />
                        <Route path="/contacts" component={Contacts}/>
                        <Route path="/signup" component={SignUp}/>
                        {this.state.applicationFormVisible
                            ? <ApplicationForm
                                closeAppWindow={this.closeApplicationForm}
                                userName={this.state.userName}
                                token={this.state.token}
                            />
                            : null
                        }
                        {this.state.logInFormVisible
                            ? <LogIn closeLogInForm={this.closeLogInForm} storeResponse={this.storeResponse}/>
                            : null
                        }
                    </div>
                </section>

            </Router>
        );
    }

    openApplicationForm = () => {
        this.setState({
            applicationFormVisible: true
        })
    };

    closeApplicationForm = () => {
        this.setState({
            applicationFormVisible: false
        })
    };

    openLogInForm = () => {
        this.setState({
            logInFormVisible: true
        })
    };

    closeLogInForm = () => {
        this.setState({
            logInFormVisible: false
        })
    };

    storeResponse = (username, token) => {
        this.setState({
            userName: username,
            token: token
        })
    };

    asAdmin = () => {
        this.setState({
            admin: true
        })
    }
}

export default App;
