import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link} from "react-router-dom"
import './resource/styles/Main.css'
import About from "./components/About";
import Rooms from "./components/Rooms";
import Contacts from "./components/Contacts"
import SignUp from "./components/SignUp";
import ApplicationForm from "./components/ApplicationForm";
import LogIn from "./components/LogIn";

class App extends Component {

    state = {
        applicationFormVisible: false,
        logInFormVisible: false,
        userName: '',
        userLogin: '',
        token: ''
    };

    render() {
        return (
            <React.Fragment>
                <Router>

                    <header>
                        <div className='container'>
                            <div id='topPanel'>
                                <button onClick={() => {this.openLogInForm()}}>Войти</button>
                            </div>
                        </div>
                    </header>

                    <nav>
                        <img src='/src/resource/img/siburLogoTemp.png'/>
                        <ul id='mainNav'>
                            <li>
                                <Link to='/'>О сервисе</Link>
                            </li>
                            <li>
                                <Link to='/rooms'>Помещения</Link>
                            </li>
                            <li>
                                <Link to='/contacts'>Контакты</Link>
                            </li>
                            <li>
                                <Link to='/signup'>Регистрация</Link>
                            </li>
                            <li>
                                <button onClick={this.openApplicationForm}>Оформить заявку</button>
                            </li>
                        </ul>
                    </nav>

                    <section id='mainContent'>
                        <div className='container'>
                            <Route
                                path="/"
                                exact
                                component={About}
                            />
                            <Route
                                path="/rooms"
                                component={Rooms}
                            />
                            <Route path="/contacts" component={Contacts}/>
                            <Route path="/signup" component={SignUp}/>
                            {this.state.applicationFormVisible
                                ? <ApplicationForm
                                    closeAppWindow={this.closeApplicationForm}
                                    userName={this.state.userName}
                                    token={this.state.token}
                                />
                                : null
                            }
                            {this.state.logInFormVisible
                                ? <LogIn closeLogInForm={this.closeLogInForm} storeResponse={this.storeResponse}/>
                                : null}
                        </div>
                    </section>

                </Router>
            </React.Fragment>
        );
    }

    openApplicationForm = () => {
        this.setState({
            applicationFormVisible: true
        })
    };

    closeApplicationForm = () => {
        this.setState({
            applicationFormVisible: false
        })
    };

    openLogInForm = () => {
        this.setState({
            logInFormVisible: true
        })
    };

    closeLogInForm = () => {
        this.setState({
            logInFormVisible: false
        })
    };

    storeResponse = (username, token) => {
        this.setState({
            userName: username,
            token: token
        })
    }
}

export default App;
