import React from 'react';
import moment from 'moment';

class Calendar extends React.Component {
    state = {
        dateContext: moment(),
        today: moment(),
        showMonthPopup: false,
        showYearPopup: false,
        selectedDay: null
    };

    weekdaysShort = moment.weekdaysShort();
    months = moment.months();

    year = () => this.state.dateContext.format("Y");

    month = () => this.state.dateContext.format("MMMM");

    daysInMonth = () => this.state.dateContext.daysInMonth();

    currentDay = () => this.state.dateContext.format("D");

    firstDayOfMonth = () => moment(this.state.dateContext).startOf('month').format('d');

    setMonth = (month) => {
        const monthNo = this.months.indexOf(month);
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("month", monthNo);
        this.setState({
            dateContext: dateContext
        });
    };

    moveMonth = (side) => {
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).add(side, "month");
        this.setState({
            dateContext: dateContext
        });
    };

    SelectList = (props) => {
        const popup = props.monthList.map((monthName) => {
            return (
                <div key={monthName}>
                    <p onClick={() => {this.setMonth(monthName)}}>
                        {monthName}
                    </p>
                </div>
            );
        });

        return (
            <div>
                {popup}
            </div>
        );
    };

    onChangeMonth = () => {
        this.setState({
            showMonthPopup: !this.state.showMonthPopup
        });
    };

    MonthNav = () => {
        return (
            <span onClick={()=> {this.onChangeMonth()}}>
                {this.month()}
                {this.state.showMonthPopup
                    ? <this.SelectList monthList={this.months} />
                    : null
                }
            </span>
        );
    };

    showYearEditor = () => {
        this.setState({
            showYearNav: true
        });
    };

    setYear = (e) => {
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("year", e.target.value);
        this.setState({
            dateContext: dateContext
        })
    };

    onKeyUpYear = (e) => {
        if (e.which === 13 || e.which === 27) {
            const userInput = e.target.value;
            this.setYear(/d+/.test(userInput) ? userInput : this.year);
            this.setState({
                showYearNav: false
            })
        }
    };

    YearNav = () => {
        return (
            this.state.showYearNav ?
                <input
                    defaultValue = {this.year()}
                    className="editor-year"
                    ref={(yearInput) => { this.yearInput = yearInput}}
                    onKeyUp= {(e) => this.onKeyUpYear(e)}
                    onChange = {(e) => this.setYear(e)}
                    type="number"
                    placeholder="year"/>
                :
                <span
                    className="label-year"
                    onDoubleClick={(e)=> { this.showYearEditor()}}>
                {this.year()}
            </span>
        );
    };

    onDayClick = (e, day) => {
        this.setState({
            selectedDay: day
        }, () => {
            console.log("SELECTED DAY: ", this.state.selectedDay);
        });

        this.props.onDayClick && this.props.onDayClick(e, day);
    };

    render() {
        // Map the weekdays i.e Sun, Mon, Tue etc as <td>
        let weekdays = this.weekdaysShort.map((day) => {
            return (
                <td key={day} className="week-day">{day}</td>
            )
        });

        let blanks = [];
        for (let i = 0; i < this.firstDayOfMonth(); i++) {
            blanks.push(<td key={i * 80} className="emptySlot">
                    {""}
                </td>
            );
        }

        console.log("blanks: ", blanks);

        let daysInMonth = [];
        for (let d = 1; d <= this.daysInMonth(); d++) {
            let className = (d == this.currentDay() ? "day current-day": "day");
            let selectedClass = (d === this.state.selectedDay ? " selected-day " : "")
            daysInMonth.push(
                <td key={d} className={className + selectedClass} >
                    <span onClick={(e)=>{this.onDayClick(e, d)}}>{d}</span>
                </td>
            );
        }


        console.log("days: ", daysInMonth);

        const totalSlots = [...blanks, ...daysInMonth];
        let rows = [];
        let cells = [];

        totalSlots.forEach((row, i) => {
            if ((i % 7) !== 0) {
                cells.push(row);
            } else {
                let insertRow = cells.slice();
                rows.push(insertRow);
                cells = [];
                cells.push(row);
            }
            if (i === totalSlots.length - 1) {
                let insertRow = cells.slice();
                rows.push(insertRow);
            }
        });

        let trElems = rows.map((d, i) => {
            return (
                <tr key={i*100}>
                    {d}
                </tr>
            );
        });

        return (
            <div className="calendar-container" style={this.style}>
                <table className="calendar">
                    <thead>
                    <tr className="calendar-header">
                        <td colSpan="5">
                            <this.MonthNav />
                            {" "}
                            <this.YearNav />
                        </td>
                        <td colSpan="2" className="nav-month">
                            <button onClick={()=> {this.moveMonth(-1)}}>prev</button>
                            <button onClick={()=> {this.moveMonth(1)}}>next</button>
                        </td>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        {weekdays}
                    </tr>
                    {trElems}
                    </tbody>
                </table>

            </div>

        );
    }
}

export default Calendar;
