import React, {Component} from 'react';
import PropTypes from 'prop-types';

class ApplicationForm extends Component {

    state = {
        eventName: '',
        eventType: '',
        room: ''
    };

    handleClick = () => {
        this.props.toggle();
    };

    appForm = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        backgroundColor: '#f4f4f4',
        borderColor: '#333',
        borderWidth: '2px',
        borderStyle: 'solid',
        padding: '20px',
        gap: '30px'
    };

    render() {
        return (
            <form id='applicationForm' style={this.appForm}>
                <label>Название мероприятия: </label>
                <input type='text' id='eventName' name='eventName'/><br/>
                <label>Название мероприятия: </label>
                <select id='eventType' name='eventType'>
                    <option value='sportsTraining'>Спортивная тренировка</option>
                    <option value='concert'>Концерт</option>
                    <option value='party'>Корпоратив или вечеринка</option>
                </select><br/>
                <input type='submit' id='submitApp' value='Отправить заявку'/>
            </form>
        );
    }
}

ApplicationForm.propTypes = {};

export default ApplicationForm;
