import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { Room, TypeOfEvent } from '/src/Enums'

class ApplicationForm extends Component {

    state = {
        isDragged: false,
        mouesStartX: 0,
        mouseStartY: 0,
        eventName: '',
        eventType: '',
        room: ''
    };

    appForm = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        backgroundColor: '#222',
        borderColor: '#333',
        borderWidth: '2px',
        borderStyle: 'solid',
        padding: '20px',
        lineHeight: '20px'
    };

    render() {
        return (
            <form id='applicationForm' style={this.appForm}>
                <button onClick={this.props.closeAppWindow}>X</button>
                <label>Название мероприятия: </label>
                <input type='text' id='eventName' name='eventName'/><br/>
                <label>Тип мероприятия: </label>
                <h1>{TypeOfEvent.params}</h1>
                <input type='submit' id='submitApp' value='Отправить заявку'/>
            </form>
        );
    }

    handleTextInput = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    };
}

ApplicationForm.propTypes = {
    closeAppWindow: PropTypes.func.isRequired
};

export default ApplicationForm;
