import React, {Component} from 'react';
import PropTypes from 'prop-types';

class TimeSelectDialogue extends Component {

    constructor(props) {
        super(props);

        this.inputsRef = [];
        for (let i = 0; i < 4; i++) {
            this.inputsRef.push(React.createRef());
        }
    }

    handleInput = (e) => {
        this.props.changeTiming(e.target.name, e.target.value)
    };

    render() {
        const { startH, startM, endH, endM } = this.props.dayToShow;
        console.log(`sh: ${startH}, sm: ${startM}, eh: ${endH}, em: ${endM}`);
        const timeAttrs = [startH, startM, endH, endM];
        const attrNames = ['startH', 'startM', 'endH', 'endM'];
        const inputs = [];
        for (let i = 0; i < timeAttrs.length; i++) {
            const maxVal = (i % 2 === 0) ? 23 : 59;
            inputs.push(
                <input
                    type='number'
                    defaultValue={timeAttrs[i]}
                    min={0}
                    max={maxVal}
                    name={attrNames[i]}
                    onChange={e => this.handleInput(e)}
                    ref={this.inputsRef[i]}
                />)
        }

        for (let i = 0; i < 4; i++) {
            this.inputsRef[i].current.value()
        }

        return (
            <div>
                <label>Время начала мероприятия: </label>
                {inputs[0]}
                <label> ч </label>
                {inputs[1]}
                <label> мин</label><br/>
                <label>Время окончания мероприятия: </label>
                {inputs[2]}
                <label> ч </label>
                {inputs[3]}
                <label> мин</label><br/>
                <input type='checkbox' onChange={(e) => {console.log(e.target.value)}}/>
                <label>Установить такие временные рамки для всех выбранных дней</label><br/>
                <button onClick={()=> {this.props.closeTimeSelectDialogue()}}>OK</button>
            </div>
        );
    }
}

TimeSelectDialogue.propTypes = {
    dayToShow: PropTypes.object.isRequired,
    changeTiming: PropTypes.func.isRequired,
    setForAll: PropTypes.func.isRequired,
    closeTimeSelectDialogue: PropTypes.func.isRequired
};

export default TimeSelectDialogue;
