import React, {Component} from 'react';
import PropTypes from 'prop-types';
import '../resource/styles/TimeSelectDialogue.css'

class TimeSelectDialogue extends Component {

    handleInput = (e) => {
        this.props.changeTiming(e.target.name, e.target.value)
    };

    render() {
        const { startH, startM, endH, endM } = this.props.dayToShow;
        const { startHRef, startMRef, endHRef, endMRef, checkBoxRef } = this.props;
        return (
            <div id='timing-container' className='block-container'>
                <label>Время начала мероприятия: </label>
                <input
                    className='time-input'
                    type='text'
                    defaultValue={startH}
                    min={0}
                    max={23}
                    name='startH'
                    onChange={e => this.handleInput(e)}
                    ref={startHRef}
                />
                <label> ч </label>
                <input
                    className='time-input'
                    type='text'
                    defaultValue={startM}
                    min={0}
                    max={59}
                    name='startM'
                    onChange={e => this.handleInput(e)}
                    ref={startMRef}
                />
                <label> мин</label><br/>
                <label>Время окончания мероприятия: </label>
                <input
                    className='time-input'
                    type='text'
                    defaultValue={endH}
                    min={0}
                    max={23}
                    name='endH'
                    onChange={e => this.handleInput(e)}
                    ref={endHRef}
                />
                <label> ч </label>
                <input
                    className='time-input'
                    type='text'
                    defaultValue={endM}
                    min={0}
                    max={59}
                    name='endM'
                    onChange={e => this.handleInput(e)}
                    ref={endMRef}
                />
                <label> мин</label><br/>
                <div id='checkbox-aligner'>
                    <input
                        type='checkbox'
                        onChange={(e) => {this.props.setForAll(e)}}
                        ref={checkBoxRef}
                        id='hidden-check'
                    />
                    <label htmlFor='hidden-check'>
                    </label>
                    <label>Установить такие временные рамки для всех выбранных дат</label><br/>
                </div>
                <button onClick={()=> {this.props.closeTimeSelectDialogue()}}>OK</button>
            </div>
        );
    }

    invalidInput = (e, where) => {
        const numeric = ['viewers'].includes(where) && !e.target.value.match(/^\d*$/);
        if (numeric) {
            if (!this.state.warning.includes(where)){
                this.setState({
                    warning: [...this.state.warning, where]
                })
            }
        } else {
            this.props.closeHint();
            const warnArray = this.state.warning.slice();
            const index = warnArray.indexOf(where);
            if (index !== -1) {
                this.setState({
                    warning: warnArray.splice(index, 1)
                })
            }
        }
    }
}

TimeSelectDialogue.propTypes = {
    dayToShow: PropTypes.object.isRequired,
    changeTiming: PropTypes.func.isRequired,
    setForAll: PropTypes.func.isRequired,
    closeTimeSelectDialogue: PropTypes.func.isRequired,
    startHRef: PropTypes.func.isRequired,
    startMRef: PropTypes.func.isRequired,
    endHRef: PropTypes.func.isRequired,
    endMRef: PropTypes.func.isRequired,
    checkBoxRef: PropTypes.func.isRequired
};

export default TimeSelectDialogue;
