import React, {Component} from 'react';
import PropTypes from 'prop-types';
import {Room, TypeOfEvent} from '../functional/EnumsAndConsts'
import Calendar from "./Calendar";
import '../resource/styles/ApplicationForm.css'

class ApplicationForm extends Component {

    state = {
        eventName: null,
        eventType: TypeOfEvent.CONCERT,
        room: Room[0].stringId,
        viewersExpected: false,
        viewers: 0,
        date: null,
        time: null
    };

    render() {
        return (
            <form id='application-form' style={this.appForm}>
                <button onClick={this.props.closeAppWindow}>X</button><br/>
                <label>Название мероприятия: </label>
                <input required type='text' id='eventName' name='eventName' onChange={this.handleInput}/><br/>
                <label>Тип мероприятия: </label>
                <select required id='eventTypeSel' name='eventType' onChange={this.handleInput}>
                    <option value={TypeOfEvent.CONCERT}>Концерт, представление</option>
                    <option value={TypeOfEvent.PARTY}>Корпоратив</option>
                    <option value='sport'>Спорт</option>
                    <option value={TypeOfEvent.OTHER}>Другое</option>
                </select><br/>
                {['sport', 'match', 'training'].includes(this.state.eventType)
                    ?
                    <React.Fragment>
                        <input name='eventType' type='radio' value='training' onChange={this.handleInput}/><label>Тренировка</label><br/>
                        <input name='eventType' type='radio' value='match' onChange={this.handleInput}/><label>Матч</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                <label>Помещение: </label>
                <select required id='roomSel' name='room' onChange={this.handleInput}>
                    {Room.map((roomInfo) =>
                        <option key={roomInfo.stringId} value={roomInfo.stringId}>{roomInfo.rusName}</option> )}
                </select><br/>
                {this.state.eventType === TypeOfEvent.OTHER
                    ?
                    <React.Fragment>
                        <label>Ожидается ли на Вашем мероприятии зрительская аудитория?</label><br/>
                        <input name='viewersExpected' type='radio' value={true} onChange={this.handleInput}/><label>Да</label><br/>
                        <input name='viewersExpected' type='radio' value={false} onChange={this.handleInput}/><label>Нет</label><br/>
                    </React.Fragment>
                    :
                    null
                }
                {['match', 'party'].includes(this.state.eventType) || this.state.viewersExpected === 'true'
                    ?
                    <React.Fragment>
                        <label>Какое количество зрителей Вы предполагаете?</label>
                        <input required type='text' id='auditoryInp' name='viewers' onChange={this.handleInput}/><br/>
                    </React.Fragment>
                    :
                    null
                }
                <Calendar/>
                <input type='submit' id='submitApp' value='Отправить заявку' onClick={this.sendApplication}/>
            </form>
        );
    }

    handleInput = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        });
    };

    sendApplication = (e) => {
        e.preventDefault();
        console.log(this.state);
    }
}

ApplicationForm.propTypes = {
    closeAppWindow: PropTypes.func.isRequired
};

export default ApplicationForm;
