import React, {Component} from 'react';
import PropTypes from 'prop-types';

class Hint extends Component {

    position = {
        top: (this.props.parentY + this.props.parentHeight + 3) + 'px',
        left: (this.props.parentX + this.props.parentWidth + 3) + 'px'
    };

    render() {
        return (
            <div className='hint' style={this.position}>
                {this.props.hintText}
            </div>
        );
    }
}

Hint.propTypes = {
    hintText: PropTypes.string.isRequired,
    parentX: PropTypes.number.isRequired,
    parentWidth: PropTypes.number.isRequired,
    parentY: PropTypes.number.isRequired,
    parentHeight: PropTypes.number.isRequired
};

export default Hint;
