import React from 'react';
import moment from 'moment';
import '../resource/styles/Calendar.css'
import TimeSelectDialogue from "./TimeSelectDialogue";
import Timing from "../functional/Timing";

class Calendar extends React.Component {

    state = {
        dateContext: moment(),
        today: moment(),
        showMonthPopup: false,
        showYearPopup: false,
        selectedDays: [],
        selectedTimings: [],
        lastChosenWithShift: null,
        timeSelectDialogue: null,
        currentlyManagedDayIndex: -1
    };

    weekdaysShort = ['ПН', 'ВТ', 'СР', 'ЧТ', 'ПТ', 'СБ', 'ВС'];
    months = ['Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь', 'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'];

    year = () => this.state.dateContext.format("Y");

    month = () => {
        const monthEng = this.state.dateContext.format("MMMM");
        // eslint-disable-next-line default-case
        switch (monthEng) {
            case 'January': {
                return 'Январь';
            }
            case 'February': {
                return 'Февраль';
            }
            case 'March': {
                return 'Март';
            }
            case 'April': {
                return 'Апрель';
            }
            case 'May': {
                return 'Май';
            }
            case 'June': {
                return 'Июнь';
            }
            case 'July': {
                return 'Июль';
            }
            case 'August': {
                return 'Август';
            }
            case 'September': {
                return 'Сентябрь';
            }
            case 'October': {
                return 'Октябрь';
            }
            case 'November': {
                return 'Ноябрь';
            }
            case 'December': {
                return 'Декабрь';
            }
        }
    };

    daysInMonth = () => this.state.dateContext.daysInMonth();

    firstDayOfMonth = () => moment(this.state.dateContext).startOf('month').format('d');

    setMonth = (month) => {
        const monthNo = this.months.indexOf(month);
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("month", monthNo);
        this.setState({
            dateContext: dateContext
        });
    };

    moveMonth = (side) => {
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).add(side, "month");
        this.setState({
            dateContext: dateContext
        });
    };

    SelectList = (props) => {
        const popup = props.monthList.map((monthName) => {
            return (
                <div key={monthName}>
                    <p onClick={() => {
                        this.setMonth(monthName)
                    }}>
                        {monthName}
                    </p>
                </div>
            );
        });

        return (
            <div id='month-select-popup'>
                {popup}
            </div>
        );
    };

    onChangeMonth = () => {
        this.setState({
            showMonthPopup: !this.state.showMonthPopup
        });
    };

    MonthNav = () => {
        return (
            <span id='month-nav' onClick={() => {
                this.onChangeMonth()
            }}>
                {this.month()}
                {this.state.showMonthPopup
                    ? <this.SelectList monthList={this.months}/>
                    : null
                }
            </span>
        );
    };

    showYearEditor = () => {
        this.setState({
            showYearNav: !this.state.showYearNav
        });
    };

    setYear = (e) => {
        const userInput = e.target.value;
        if (/\d+/.test(userInput)) {
            let dateContext = Object.assign({}, this.state.dateContext);
            dateContext = moment(dateContext).set("year", e.target.value);
            this.setState({
                dateContext: dateContext
            });
        }
    };

    onKeyUpYear = (e) => {
        if (e.which === 13 || e.which === 27) {
            this.setYear(e);
            this.setState({
                showYearNav: false
            })
        }
    };

    YearNav = () => {
        return (
            this.state.showYearNav
                ?
                <span>
                    <input
                        defaultValue={this.year()}
                        onKeyUp={(e) => this.onKeyUpYear(e)}
                        type="number"
                    />
                    <button
                        className='transparent-element no-border-element'
                        onClick={() => {
                            this.showYearEditor()
                        }}
                    >
                        OK
                    </button>
                </span>
                :
                <span
                    onClick={() => {
                        this.showYearEditor()
                    }}
                    id='year-nav'
                >
                {this.year()}
                </span>
        );
    };

    onDayClick = (e, day) => {
        if (e.target.className.indexOf('time-set-button') === -1) {
            if (e.shiftKey) {
                const additionDays = [];
                const additionTimings = [];
                if (this.state.lastChosenWithShift === null) {
                    if (!this.state.selectedDays.includes(day)) {
                        additionDays.push(day);
                        additionTimings.push(new Timing());
                    }
                    this.setState({
                        lastChosenWithShift: day,
                        selectedDays: [...this.state.selectedDays, ...additionDays],
                        selectedTimings: [...this.state.selectedTimings, ...additionTimings]
                    });
                } else {
                    if (this.state.lastChosenWithShift < day) {
                        for (let i = this.state.lastChosenWithShift + 1; i <= day; i++) {
                            if (!this.state.selectedDays.includes(i)) {
                                additionDays.push(i);
                                additionTimings.push(new Timing());
                            }
                        }
                    } else {
                        for (let i = day; i <= this.state.lastChosenWithShift - 1; i++) {
                            if (!this.state.selectedDays.includes(i)) {
                                additionDays.push(i);
                                additionTimings.push(new Timing());
                            }
                        }
                    }
                    this.setState({
                        lastChosenWithShift: null,
                        selectedDays: [...this.state.selectedDays, ...additionDays],
                        selectedTimings: [...this.state.selectedTimings, ...additionTimings]
                    })
                }
            } else {
                const newSelectedDays = this.state.selectedDays.slice();
                const newSelectedTimings = this.state.selectedTimings.slice();
                const index = newSelectedDays.indexOf(day);
                if (index > -1) {
                    newSelectedDays.splice(index, 1);
                    newSelectedTimings.splice(index, 1);
                } else {
                    newSelectedDays.push(day);
                    newSelectedTimings.push(new Timing());
                }
                this.setState({
                    selectedDays: newSelectedDays,
                    selectedTimings: newSelectedTimings
                });
            }
            console.log(this.state.selectedDays.indexOf(day));
        }
    };

    render() {
        const weekdays = this.weekdaysShort.map((day) => {
            return (
                <td key={day} className='weekday'>{day}</td>
            )
        });

        const blanks = [];
        const realBlanksLen = +this.firstDayOfMonth() === 0 ? 7 : +this.firstDayOfMonth();
        for (let i = 1; i < realBlanksLen; i++) {
            blanks.push(<td key={i * 80}>{""}</td>);
        }

        const daysInMonth = [];
        for (let d = 1; d <= this.daysInMonth(); d++) {
            let dayClass = 'day';
            if (this.state.selectedDays.includes(d)) {
                dayClass += ' selected-day';
            } else {
                dayClass += ' non-selected-day';
                const dayOfWeek = (blanks.length + d) % 7;
                dayClass += (dayOfWeek === 0 || dayOfWeek === 6) ? ' non-selected-weekend' : ' non-selected-weekday'
            }
            daysInMonth.push(
                <td
                    key={d} onClick={(e) => {
                    this.onDayClick(e, d)
                }}
                    className={dayClass}
                >
                    {d}
                    <button
                        className={this.getTimeSetButtonClass(d)}
                        onClick={
                            (e) => {
                                this.timeSetButton(e, d)
                            }}>
                    </button>
                </td>
            );
        }

        const totalSlots = [...blanks, ...daysInMonth];
        const rows = [];
        let cells = [];

        totalSlots.forEach((row, i) => {
            if ((i % 7) !== 0) {
                cells.push(row);
            } else {
                rows.push(cells.slice());
                cells = [];
                cells.push(row);
            }
            if (i === totalSlots.length - 1) {
                rows.push(cells.slice());
            }
        });

        const trElements = rows.map((d, i) => {
            return (
                <tr key={i * 100}>
                    {d}
                </tr>
            );
        });

        return (
            <div id='calendar-container'>
                <table id='calendar-table' className='transparent-element'>
                    <thead>
                    <tr>
                        <td colSpan="5">
                            <this.MonthNav/>
                            {" "}
                            <this.YearNav/>
                        </td>
                        <td colSpan="2">
                            <button onClick={() => {
                                this.moveMonth(-1)
                            }}>prev
                            </button>
                            <button onClick={() => {
                                this.moveMonth(1)
                            }}>next
                            </button>
                        </td>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        {weekdays}
                    </tr>
                    {trElements}
                    </tbody>
                </table>
                {this.state.timeSelectDialogue === null
                    ? null
                    : <TimeSelectDialogue
                        dayToShow={this.state.timeSelectDialogue}
                        changeTiming={this.changeTiming}
                        closeTimeSelectDialogue={() => console.log('close')}
                        setForAll={() => console.log('set for all')}/>}
            </div>

        );
    }

    changeTiming = (timing) => {
        const newTimings = this.state.selectedTimings.slice();
        newTimings[this.state.currentlyManagedDayIndex].setTiming(timing);
        this.setState({
            selectedTimings: newTimings
        })
    };

    timeSetButton = (e, day) => {
        if (!this.state.selectedDays.includes(day)) {
            this.setState({
                selectedDays: [...this.state.selectedDays, day],
                selectedTimings: [...this.state.selectedTimings, new Timing()]
            })
        }

    };

    timeSetButtonPt2 = (e, day) => {
        if (!e.shiftKey) {
            const index = this.state.selectedDays.indexOf(day);
            console.log(`Day u r trying to manage is ${index}th in selectedDays`);
            this.setState({
                timeSelectDialogue: this.state.selectedTimings[index],
                currentlyManagedDayIndex: index
            })
        }
    };

    getTimeSetButtonClass = (day) => {
        return this.state.selectedDays.includes(day)
            ? 'time-set-button time-set-button-orange'
            : 'time-set-button time-set-button-white'
    }
}

export default Calendar;
