import React, {Component} from 'react';
import PropTypes from 'prop-types';

class SelectList extends Component {

    componentWillMount() {
        document.addEventListener('click', e => this.props.clickOutside(e), false);
    }

    componentWillUnmount() {
        document.removeEventListener('click', this.props.clickOutside(e), false);
    }

    render() {
        const popup = this.props.options.map((option) => {
            return (
                <p
                    key={option.rusName}
                    className='hover-text'
                    onClick={() => {this.props.onChoose(option)}}
                >
                    {option.rusName}
                    {option.additional}
                </p>
            );
        });
        return (
            <div className='select-popup'>
                {popup}
            </div>
        );
    }
}

SelectList.propTypes = {
    options: PropTypes.array.isRequired,
    onChoose: PropTypes.func.isRequired,
    clickOutside: PropTypes.func.isRequired
};

export default SelectList;
