import React, {Component} from 'react';
import PropTypes from 'prop-types';

class ApplicationForm extends Component {

    state = {
        isDragged: false,
        mouesStartX: 0,
        mouseStartY: 0,
        eventName: '',
        eventType: '',
        room: ''
    };

    appForm = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        backgroundColor: '#f4f4f4',
        borderColor: '#333',
        borderWidth: '2px',
        borderStyle: 'solid',
        padding: '20px',
        lineHeight: '20px'
    };

    closeButton = {
        backgroundColor: 'red',
        color: 'white'
    };

    render() {
        return (
            <form id='applicationForm' style={this.appForm} onMouseDown={this.setDragged} onMouseMove={this.Drag}>
                <button style={this.closeButton} onClick={this.props.closeAppWindow}>X</button>
                <label>Название мероприятия: </label>
                <input type='text' id='eventName' name='eventName' onChange={this.handleTextInput}/><br/>
                <label>Название мероприятия: </label>
                <select id='eventType' name='eventType'>
                    <option value='sportsTraining'>Спортивная тренировка</option>
                    <option value='concert'>Концерт</option>
                    <option value='party'>Корпоратив или вечеринка</option>
                </select><br/>
                <input type='submit' id='submitApp' value='Отправить заявку'/>
            </form>
        );
    }

    handleTextInput = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    };

    setDragged = (e) => {
        console.log(e)
    };

    Drag = () => {}
}

ApplicationForm.propTypes = {
    closeAppWindow: PropTypes.func.isRequired
};

export default ApplicationForm;
